mapper.select:
select A.*
from(
select (select
count(*) from notice ) + 1 - @rownum:=@rownum+1 as rownum, N.notice_id,
N.notice_title, N.notice_content, N.date, N.manager
from notice N, (select @rownum:=0)TMP
order by
rownum desc
)A
where A.rownum between #{start} and #{end};

select
A.*
from(
select (select count(*) from notice where notice_title like CONCAT('%',#{notice_title},'%')) + 1 -
@rownum:=@rownum+1 as rownum, N.notice_id, N.notice_title, N.notice_content, N.date, N.manager
from (select * from notice where notice_title like CONCAT('%',#{notice_title},'%'))N, (select @rownum:=0)TMP
order by rownum desc
)A
where A.rownum between #{start} and #{end};

select
	A.*
	from(
	select (select count(*) from notice where notice_content like CONCAT('%',#{word},'%')) + 1 -
	@rownum:=@rownum+1 as rownum, N.notice_id, N.notice_title, N.notice_content, N.date, N.manager
	from (select * from notice where notice_content like CONCAT('%',#{word},'%'))N, (select @rownum:=0)TMP
	order by rownum desc
	)A
	where A.rownum between #{start} and #{end};

mapper.update:
update notice
set notice_title = #{notice_title}, notice_content = #{notice_content}
where notice_id = #{notice_id}
